sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
        this.rightnow = new GlideDateTime();
    },

    isDatePast: function(strDate) {
        // Create GlideDateTime objects for the current date and the passed in date

        var testdate = new GlideDateTime(strDate);

        // If the testdate is before rightnow, return true, else return false
		//gs.info('rightnow é ' + rightnow);
		gs.info('this.rightnow é ' + this.rightnow);

        if (testdate.before(this.rightnow)) {
            return true;
        } else {
            return false;
        }
    },
    isDateToday: function(strDate) {
        var today = this.rightnow.getLocalDate();
        var isToday = new GlideDateTime(strDate);
		gs.info ("is today" + isToday);		
		var isTodayAux = isToday.getLocalDate();
		gs.info ("is today" + isTodayAux);

        if (today.compareTo(isTodayAux) == 0) {
            return true;
        } else {
            return false;
        }
    },

    type: 'NeedItUtils'
};
